import blog1 from './assets/images/dummy/blog-1.svg';
import blog2 from './assets/images/dummy/blog-2.svg';
import blog3 from './assets/images/dummy/blog-3.svg';
import BlogItem from './BlogItem';

const Blog = () => {

  const blogItem = [
    {
      image: blog1,
      title: 'Disease detection, check up in the laboratory',
      desc: 'In this case, the role of the health laboratory is very important to do a disease detection...',
    },
    {
      image: blog1,
      title: 'Herbal medicines that are safe for consumption',
      desc: 'Herbal medicine is very widely used at this time because of its very good for your health',
    },
    {
      image: blog1,
      title: 'Natural care for healthy facial skin',
      desc: 'A healthy lifestyle should start from now and also for your skin health. There are some',
    }
  ]


  return (
    <div class="blog" id="about-us">
      <div class="container">
        <div class="blog__title">
          <h3 class="text-title">Check out our latest article</h3>
        </div>
        <div class="blog__content">
          <div class="blog__list">
            <div class="blog__item">
              <div class="blog__box">
              <a class="blog__link" href="#blog-detail"></a>
                <div class="blog__img">
                  <img class="blog__img__el" src="assets/img/dummy/blog-1.jpg" alt="Disease detection, check up in the laboratory" />
                </div>
                <div class="blog__txt">
                  <h3 class="blog__txt__title">Disease detection, check up in the laboratory</h3>
                  <p class="blog__txt__desc">In this case, the role of the health laboratory is very important to do a disease detection...</p>
                  <button class="btn-forward" type="button">Read more
                    <i class="icon-arrow-right"></i>
                  </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
    </div>
  )
}

export default Blog;
