const Button = (props) => {
  const {
    to,
    children,
    type = "button",
    variant = "primary",
    block = false,
  } = props;

  // const classNames = cn('btn,' `btn--${variant}`, {
  //   `btn--forward`: block,
  // });

  return (
    <>
      {type === "link" && (
        <a className={classNames} href={to}>
          {children}
        </a>
      )}
      {type !== "link" && (
        <button type={type} className={classNames} href={to}>
          {children}
        </button>
      )}
    </>
  );
};

export default Button;
