import React, { Component } from "react";

class Tambahan extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showPopup: false,
      fullName: "John Doe",
    };
  }

  handleChangeFullName = (e) => {
    const _value = e.target.value;
    this.setState({
      fullName: _value,
    });
