import avatar1 from './assets/images/dummy/testimonials-1.jpg';
import avatar2 from './assets/images/dummy/testimonials-2.jpg';
import avatar3 from './assets/images/dummy/testimonials-3.jpg';
import avatar4 from './assets/images/dummy/testimonials-4.jpg';
import TestimonialsItem from './TestimonialsItem';


const Testimonials = () => {

  const testimonialsItem = [
    {
      image: 'avatar1',
      name: 'Edward Newgate',
      position: 'Founder Circle',
      caption: "Our dedicated patient engagement app and web portal allow you to access information instantaneously (no tedeous form, long calls, or administrative hassle) and securely"
    },
  ]

  return (
    <div class="testimonials" id="testimonials">
      <div class="container">
        <div class="testimonials__box">
          <div class="testimonials__head">
            <h3 class="text-title text-title--white">What our customer are saying</h3>
          </div>
          <div class="testimonials__body js-testimonials">
            { testimonialsItem.map((v, i) => {
              return (
                <TestimonialsItem key={`t-${i}`} image={v.image} title={v.title} desc={v.desc} />
              )
            })}
          </div>
        </div>
      </div>
    </div>
  )
}

export default Testimonials;
