import cn from "classnames";

const Button = (props) => {
  const {
    to,
    children,
    type = "button",
    variant = "primary",
    arrowIcon = false,
    block = false,
  } = props;

  const classNames = cn("btn", `btn--${variant}`, {
    "btn--block": block,
  });

  return (
    <>
      {type === "link" && (
        <a className={classNames} href={to}>
          {children}
          {arrowIcon && <i class="icon-arrrow-down"></i>}
        </a>
      )}
      {type !== "link" && (
        <button type={type} className={classNames} href={to}>
          {children}
          {arrowIcon && <i class="icon-arrrow-down"></i>}
        </button>
      )}
    </>
  );
};

export default Button;
